def unicotipo(ataque,tipo):
    return tablatipos[ataque].get(tipo)

def dobletipo(ataque,tip1,tip2):
    return tablatipos[ataque].get(tip1)*tablatipos[ataque].get(tip2)

def mostrareficacia(eficacia):
    if eficacia ==0:
        print("Ataque nulo")
    elif eficacia==0.25:
        print("Ataque muy poco eficaz")
    elif eficacia ==0.5:
        print("Ataque poco eficaz")
    elif eficacia == 1:
        print("Ataque eficaz")
    elif eficacia == 2:
        print("Ataque supereficaz")
    elif eficacia == 4:
        print("Ataque muy supereficaz")

